import React, {useState, useContext, useEffect} from 'react';
import Layaout from '../components/Layaout/layaout';
import Buscador from '../components/UI/buscador'
import Seccion from '../components/Dinamicos/seccion'
import Link from 'next/link';
//firebase
import {FirebaseContext} from '../firebase' // context

const Medicinales = () => {
  const [plantas, setPlantas] = useState([]);
  const {firebase} = useContext(FirebaseContext);
  const tipo = 'Medicinal';
  useEffect(() => {
    const obtenerPlantas = () =>{
        firebase.db.collection('plantas').where(tipo, '==', true).onSnapshot(manejarSnapShot)
    }
     obtenerPlantas();
  }, []);
  // funcion que manipula el snapshot
  function manejarSnapShot (snapshot){
    const plantas = snapshot.docs.map(doc => {
      return {
        id: doc.id,
        ...doc.data()
      };
    });
    // enviar el resultado de la conultaal state
    setPlantas(plantas);
  }
    const img = "static/imgs/hiperico.jpg";
    const nombreSeccion = 'Medicinales';
    const querySeccion = 'Medicinal' ;
    return (
        <Layaout>
            <Seccion
              seccion = {nombreSeccion}
              seccionDB = {querySeccion}
              imgSeccion = {img}
            />
        </Layaout>
    )
}

export default Medicinales
