import React from 'react';
import Layaout from '../components/Layaout/layaout'
//validaciones
import useValidacion from '../hooks/useValidacion'
import validarCrearCuenta from '../validacion/validarCrearCuenta'
// State Inicial
const STATE_INICIAL = {
    nombre: '',
    email: '',
    pasword: '',
    name:''
}

const crearCuenta = () => {
    //
    const {valores, errores, submitForm, handleChange, handleSubmit,} = useValidacion(STATE_INICIAL, validarCrearCuenta, crearCuenta)
    
    const {nombre, email, password,name } = valores;
    function crearCuenta(){
        console.log('creaando cuenta...')
    }
    return ( 
        <>
            <Layaout>
                <h2>Crear Cuenta</h2>
            </Layaout>
            <div className="d-flex flex-row bd-highligh justify-content-center">
                <form 
                    onSubmit = {handleSubmit}
                >
                    <div className="form-group">
                        <label >Nombre Usurio</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            
                            placeholder="Nombre usuario"
                            value ={nombre}
                            onChange ={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label >Email address</label>
                        <input 
                            type="e-mail" 
                            className="form-control" 
                            
                            placeholder="Enter email"
                            value ={email}
                            onChange ={handleChange}
                        />
                        <small 
                        id="emailHelp" 
                        className="form-text text-muted"
                        >We'll never share your email with anyone else.</small>
                    </div>
                    <div className="form-group">
                        <label >Password</label>
                        <input 
                            type="password" 
                            className="form-control" 
                            
                            placeholder="Clave"
                            value ={password}
                            onChange ={handleChange}
                            />
                           
                    </div>

                    <button 
                        type="submit" 
                        value= "crear cuenta"
                        className="btn btn-primary "
                        >Crear</button>
                </form>
            </div>
            
        </>
     );
}
 
export default crearCuenta
