        console.log('creaando cuenta...')
    }
    return ( 
        <>
            <Layaout>
                <h2>Crear Cuenta</h2>
            </Layaout>
            <div className="d-flex flex-row bd-highligh justify-content-center">
                <form 
                    
                >
                <div className="form-group">
                        <label htmlFor ="Nombre">Nombre Usurio</label>
                        <input 
                            type="name" 
                            className="form-control" 
                            id="InputName" 
                            placeholder="Escribe tu nombre de ususrio"
                            //value ={nombre}
                            //onChange ={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor ="E-mail">Email address</label>
                        <input 
                            type="email" 
                            className="form-control" 
                            id="InputEmail1" 
                            placeholder="Enter email"
                            //value ={email}
                            //onChange ={handleChange}
                        />
                        <small 
                        id="emailHelp" 
                        className="form-text text-muted"
                        >We'll never share your email with anyone else.</small>
                    </div>
                    <div className="form-group">
                        <label >Password</label>
                        <input 
                            type="password" 
                            className="form-control" 
                            id="InputPassword1" 
                            placeholder="Password"
                            value ={password}
                            onChange ={handleChange}
                            />
                           
                    </div>

                    <button 
                        type="submit" 
                        value= "crear cuenta"
                        className="btn btn-primary "
                        >Crear</button>
                </form>
            </div>
            
        </>
     );
}
 
export default crearCuenta
