import React, { useState } from 'react';
import Layaout from '../components/Layaout/layaout'

import firebase from '../firebase';
//validaciones
import useValidacion from '../hooks/useValidacion'
import validarCrearCuenta from '../validacion/validarCrearCuenta'
// State Inicial
const STATE_INICIAL = {
    nombre: '',
    email: '',
    password: '',
}


const crearCuenta = () => {
    //
    //const {valores, errores, submitForm, handleChange, handleSubmit,} =  useValidacion(STATE_INICIAL, validarCrearCuenta, crearCuenta)
    //const {nombre, email, password} = valores;
    const[cuentaNueva, setCuentaNueva] = useState(STATE_INICIAL);
    const {nombre, email, password} = cuentaNueva;

    // Funcion que se ejecuta cuando el usuraio escribe
    const handleChange= e =>{
        console.log('state', valores)
        setCuentaNueva({
            ...cuentaNueva,
            [e.target.name] : e.target.value
        })
        console.log('cambio state', valores)
    }

    async function crearCuenta(){
        console.log('creaando cuenta...')
        try{    
            await firebase.registrar(nombre, email, password);
        }catch(error){
            console.error('Hubo un error al crear el usuario', error);
        }
    }
    // funcion que se ejecuta conforme el ususuario escribe en un campo
    return ( 
        <>
            <Layaout>
                <h2>Crear Cuenta</h2>
            </Layaout>
            <div className="d-flex flex-row bd-highligh justify-content-center">
                <form 
                    //onSubmit = {handleSubmit}
                    
                >
                    <div className="form-group">
                        <label >Nombre Usurio</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            
                            placeholder="TÃ¹ usuario"
                            //value ={nombre}
                            //onChange ={handleChange}
                        />
                        {errores.nombre && <p>{errores.nombre}</p>}
                    </div>
                    <div className="form-group">
                        <label >Email address</label>
                        <input 
                            type="e-mail" 
                            className="form-control" 
                            
                            placeholder="Enter email"
                           // value ={email}
                            //onChange ={handleChange}
                        />
                        {errores.email && <p>{errores.email}</p>}
                        <small 
                        id="emailHelp" 
                        className="form-text text-muted"
                        >We'll never share your email with anyone else.</small>
                    </div>
                    <div className="form-group">
                        <label >Password</label>
                        <input 
                            type="password" 
                            className="form-control" 
                            
                            placeholder="Clave"
                            //value ={password}
                            //onChange ={handleChange}
                        />
                         {errores.password && <p>{errores.password}</p>}  
                    </div>

                    <button 
                        type="submit" 
                        value= "crear cuenta"
                        className="btn btn-primary "
                        >Crear</button>
                </form>
            </div>
            
        </>
     );
}
 
export default crearCuenta
