export default function validarCrearCuenta(valores){
    let errores = {};

    

    // validar el password
    if(!valores.password){
        errores.password = "El Password es obligtorio"
    }else if( valores.password.length < 6 ){
        errores.password = 'el password debe tener al menos 6 caracteres'
    }

    return errores;
}
