import React, {useState, useContext, useEffect} from 'react';
import Layaout from '../components/Layaout/layaout';
import CardPlanta from '../components/cards/cardPlanta'
import Link from 'next/link';
//firebase
import {FirebaseContext} from '../firebase' // context

const Medicinales = () => {
  const [plantas, setPlantas] = useState([]);
  const {firebase} = useContext(FirebaseContext);

  useEffect(() => {
    const obtenerPlantas = () =>{
        firebase.db.collection('plantas').orderBy('nombre', 'desc').onSnapshot(manejarSnapShot)
    }
     obtenerPlantas();
  }, []);
  function manejarSnapShot (snapshot){
    const plantas = snapshot.docs.map(doc => {
      return {
        id: doc.id,
        ...doc.data()
      };
    });
    console.log('plantas', plantas);
    setPlantas(plantas);
  }

    return (
        <Layaout>
            <div>  
        <div className="banner-div">
          <img src="static/imgs/hiperico.jpg" alt="" className="banner" />
          <div className="bannerText">
            <h1>Plantas Medicinales</h1>
          </div>
        </div>
        <div id="fh5co-features">
          <div className="container">
            <input className="buscaSeccion" type="text" placeholder="Search.." />
            <div className="row">
             
              
              <div className="col-md-4 animate-box">
                <div className="feature-left">
                  <img className="plant_img" src="static/imgs/Plantas/caballo.jpg" alt="" />
                  <div className="feature-copy">
                    <p>Facilis ipsum reprehenderit nemo molestias. Aut cum mollitia reprehenderit.</p>
                    <Link href="/cola_de_caballo"><p><a href="#">Learn More <i className="icon-arrow-right22" /></a></p></Link>
                    <h3>Cola de Caballo</h3>
                  </div>
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-md-4 animate-box">
                <div className="feature-left">
                  <img className="plant_img" src="static/imgs/Plantas/tomillo.jpg" alt="" />
                  <div className="feature-copy">
                    <p>Facilis ipsum reprehenderit nemo molestias. Aut cum mollitia reprehenderit.</p>
                    <Link href="/Tomillo"><p><a href="#">Learn More <i className="icon-arrow-right22" /></a></p></Link>
                    <h3>Tomillo</h3>
                  </div>
                </div>
              </div>
              
              
              {plantas.map(element =>(
                <CardPlanta
                  //states
                  key = {element.id}
                  planta = {element}
               />
             ))}
            </div>
          </div>
        </div>
      </div>
      
      
        </Layaout>
    )
}

export default Medicinales
