export default function validarNuevoCultivo(valores){
    let errores = {};

    // validar Planta
    if(!valores.planta){
        errores.email = "La planta es obligtorio"
    }

    // validar la fecha inicial
    if(!valores.fechaIni){
        errores.password = "La fecha es obligatoria"
    }

    return errores;
}
