import React, {useState, useContext, useEffect} from 'react';
import Layaout from '../components/Layaout/layaout';
import Buscador from '../components/UI/buscador'
import CardPlanta from '../components/Dinamicos/cardPlanta'
import Link from 'next/link';
//firebase
import {FirebaseContext} from '../firebase' // context

const Medicinales = () => {
  const [plantas, setPlantas] = useState([]);
  const {firebase} = useContext(FirebaseContext);

  useEffect(() => {
    const obtenerPlantas = () =>{
        firebase.db.collection('plantas').orderBy('nombre', 'desc').onSnapshot(manejarSnapShot)
    }
     obtenerPlantas();
  }, []);
  function manejarSnapShot (snapshot){
    const plantas = snapshot.docs.map(doc => {
      return {
        id: doc.id,
        ...doc.data()
      };
    });
    console.log('plantas', plantas);
    setPlantas(plantas);
  }

    return (
        <Layaout>
            <div>  
              <div className="banner-div">
                <img src="static/imgs/hiperico.jpg" alt="" className="banner" />
                <div className="bannerText">
                  <h1>Plantas Medicinales</h1>
                </div>
              </div>
              <div id="fh5co-features">
                <div className="container">
                  <Buscador/>
                  <div className="row">
                    
                    {plantas.map(element =>(
                      <CardPlanta
                        //states
                        key = {element.id}
                        planta = {element}
                     />
                   ))}
                  </div>
                </div>
              </div>
            </div>
        </Layaout>
    )
}

export default Medicinales
