import React, { useState, useContext } from 'react';
import Layaout from '../components/Layaout/layaout'
import Router from 'next/router'
import firebase from '../firebase';
// para aÃ±adir imagenes
import FileUploader from 'react-firebase-file-uploader'
//validaciones
import useValidacion from '../hooks/useValidacion'
import validarNuevoCultivo from '../validacion/validarNuevoCultivo'
import {FirebaseContext} from '../firebase' // context
// State Inicial
const STATE_INICIAL = {
    planta: '',
    alias: '',
    fechaIni: '',
    descripcion: '',
    url : '',
}

const nuevoCultivo = () => {
    // State Imagenes
    const [nombreImagen, setNombreImagen] = useState('');
    const [subiendo, setSubiendo] = useState(false);
    const [progreso, setProgreso] = useState(0);
    const [urliImagen,setUrlImagen] = useState ('')

    const {valores, errores, handleChange, handleSubmit} =  useValidacion(STATE_INICIAL, validarNuevoCultivo, agregarCultivo);
    const {planta, alias, fechaIni, imagen ,url, descripcion } = valores;
    // context con las crud de firebase
    const {usuario, firebase} = useContext(FirebaseContext);
    
    const handleUploadStart = () =>{
        setProgreso(0);
        setSubiendo(true);
    }

    const handleProgress = progress => this.setState({progreso});
    
    const handleUploadError = error => {{
        setSubiendo(error);
        console.error(error);
      };
      console.error(error);
    };
  
    const handleUploadSuccess = async filename => {
        setProgreso(100);
        setSubiendo(false);
        setNombreImagen(nombreImagen)
      firebase
        .storage()
        .ref("cultivos")
        .child(nombre)
        .getDownloadURL()
        .then(url =>{ 
            console.log(url);
            setUrlImagen(url);
        });    
    };

    async function agregarCultivo(){
            console.log('agregando cultivo...')
            if(!usuario){
                return Router.push('/login');
            }
            // Crear cultivo
            const cultivo = {
                planta: {planta},
                alias: {alias},
                fechaIni: {fechaIni},
                descripcion: {descripcion},
                imagen: '',
                comentarios: [],
                votos: 0
            }
            //insertar en la base de datos
            firebase.db.collection('cultivos').add(cultivo);
        }

    return ( 
        <>
            <Layaout>
                <h2  className="d-flex justify-content-center">Nueo Cultivo</h2>
                <div className="d-flex flex-row bd-highligh justify-content-center">
                    <form 
                        onSubmit = {handleSubmit}
                    >
                      <div className="form-group">
                            <label >Planta</label>
                            <input 
                                type="text" 
                                className="form-control" 
                                name = "planta"
                                placeholder="Tipo de planta"
                                value = {planta}
                                onChange ={handleChange}
                            />
                            {errores.planta && <p>{errores.planta}</p>}
                        </div>
                        <div className="form-group">
                            <label >Alias</label>
                            <input 
                                type="text" 
                                className="form-control" 
                                name = "alias"
                                placeholder="alias de tu planta"  
                                value = {alias}
                                onChange ={handleChange}
                            />
                            <small 
                             
                            className="form-text text-muted"
                            >Dale un alias a tu cultivo.</small>
                        </div>

                        <div className="form-group">
                            <label >Descripcion Cultivo</label>
                            <FileUploader 
                                accept ="image*/*"
                                name = "imagen"
                                value = {imagen}
                                onChange ={handleChange}
                                randomizeFilename
                                storageRef={firebase.storage().ref("images")}
                                onUploadStart={handleUploadStart}
                                onUploadError={handleUploadError}
                                onUploadSuccess={handleUploadSuccess}
                                onProgress={handleProgress}
                                multiple

                            />  
                        </div>

                        <div className="form-group">
                            <label >Fecha Inicial</label>
                            <input 
                                type="date" 
                                className="form-control" 
                                name = "fechaIni"
                                value = {fechaIni}
                                onChange ={handleChange}
                            />  
                            {errores.fechaIni && <p>{errores.fechaIni}</p>}
                        </div>
                        <div className="form-group">
                            <label >Descripcion Cultivo</label>
                            <input 
                                className="form-control" 
                                name = "descripcion"
                                value = {descripcion}
                                onChange ={handleChange}
                            />  
                        </div>
                        <button 
                            type="submit" 
                            value= "crear cultivo"
                            className="btn btn-lg btn-primary btn-block btn-signin"
                        >Agregar Cultivo</button>  
                    </form> 
                </div>
            </Layaout> 
        </>
     );
}
 
export default nuevoCultivo;
